import { DOCUMENT } from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import { MenuService } from 'src/app/services/menu.service';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
export class MainComponent implements OnInit {
    logoUrl = ""
    constructor(@Inject(DOCUMENT) public document: Document, private menuService: MenuService){};
    ngOnInit(): void {
        document.addEventListener('DOMContentLoaded', function() {
            var currentDate = new Date().toLocaleString().split(",")[0]
            document.getElementById('currentDate')!.textContent = currentDate;
        });
    }
}
<section class = "main-section" id="about">
    <div class = "intro">
        <img src="{{logoUrl}}" alt="Linh Do" class="logo" id = "logo"/>
        <h1>Hi! I'm Linh</h1>
        <p>I am an aspiring software engineer, with interests in statistics and AI/ML.</p>
        <p>The projects I am working on revolve around full-stack development, applications of Computer Vision, and statistical analysis.</p>
    </div>
</section>
<section class = "main-section bg-green" id="projects">
    <div class = "intro">
        <h1>My projects</h1>
    </div>
    <div class = "row">

    </div>
</section>
<section class = "main-section bg-black" id="contact">
    <div class = "intro">
        <h1>Contact</h1>

        <p>Made with <img src="../../../assets/icons/heart.svg" class = "filter-white" width="15px"/> by Linh Do</p>
        <p>Last updated <span id="currentDate"></span></p>
    </div>
</section>
