import { Component, OnInit } from '@angular/core';
import { Project } from 'src/app/models/project';
import { ProjectListService } from 'src/app/services/project-list.service';

@Component({
  selector: 'app-project-list',
  templateUrl: './project-list.component.html',
  styleUrls: ['./project-list.component.css']
})
export class ProjectListComponent implements OnInit {
    projects!: Project[];

    constructor(private projectListService: ProjectListService){}

    ngOnInit(): void {
        this.projectListService.getProjects().subscribe(data => {
            this.projects = data;
        })
    }

    openProjectDescription(projectIndex: number){

    }
}
<div class = "project-wrapper">
    <div class = "project" *ngFor = "let project of projects; let i=index">
        <div class = "openner" (click)="openProjectDescription(i)">
            <span class = "index">{{ i + 1 }}</span>
            <span class = "project-date">{{ project.dateCreated.toString().split("-")[0] }}</span>
            <h3>{{ project.title }}</h3>
            <img src="{{ project.imageUrl }}" />
        </div>
        <div class = "project-content-bg"></div>
        <div class = "project-content" id="project{{i}}"></div>
    </div>
</div>
