import { DOCUMENT } from "@angular/common";
import { Inject, Injectable } from "@angular/core";
import { BehaviorSubject } from "rxjs";

@Injectable({
  providedIn: "root",
})
export class MenuService {
    isDayToggle: BehaviorSubject<boolean> = new BehaviorSubject<boolean>();
  constructor(@Inject(DOCUMENT) private document: Document) {}

  toggleNightDayState() {
    const mainSections = this.document.querySelectorAll(".main-section");
    if (isDayToggle) {
      mainSections.forEach((el) => {
        el.classList.add("night-theme");
      });
      isDayToggle = false;
    } else {
      mainSections.forEach((el) => {
        el.classList.remove("night-theme");
      });
      isDayToggle = true;
    }
  }
}
