import { DOCUMENT } from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { MenuService } from 'src/app/services/menu.service';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent implements OnInit {
    isNightToggle!: BehaviorSubject<boolean>;

    constructor(@Inject(DOCUMENT) private document: Document, private menuService: MenuService){}

    ngOnInit(): void {
        this.isNightToggle = this.menuService.isDayToggle
    }

    handleNightDayClick(){
        this.menuService.toggleNightDayState();
        this.isNightToggle = this.menuService.isDayToggle;

        this.isNightToggle.subscribe(data => {
            if (data){
                var mainSections = this.document.getElementsByClassName("main-section");
                main
            }
        })

    }

}
<header>
    <div class = "connect">
        <button id = "nightDayToggle" (click)="handleNightDayClick()">
            <img *ngIf = "!isNightToggle.getValue()" src="../../../assets/icons/moon.svg">
            <img *ngIf = "isNightToggle.getValue()" src="../../../assets/icons/sun.svg">
        </button>
        <a  href = "https://drive.google.com/file/d/10CqG6FMyzOzAVt5XWzdDu9MJFKH6nYOj/view?usp=sharing"
            target="_blank">Resume</a>
        <a  href = "https://github.com/LynnHaDo" 
            target="_blank">Github</a>
    </div>
</header>
<div class="wrapper">
    <ul class="menus">
        <li><a href="#about" alt="About">
            <img src="../../../assets/icons/user.svg" />
        </a></li>
        <li><a href="#projects" alt="Projects">
            <img src="../../../assets/icons/code.svg" />
        </a></li>
        <li><a href="#contact" alt="Contact">
            <img src="../../../assets/icons/send.svg" />
        </a></li>
    </ul>
</div>
